function AllTodo(props) {
  const [todos, settodos] = useState([]);
  const [todoTask, settodoTask] = useState("");
  const [isInserted, setisInserted] = useState(false);
  const [getOneT, setOneTodo] = useState(null);
  const [todo, settodo] = useState(0);
  const [isDeleteAll, setIsDeleteAll] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  useEffect(() => {
    const getAll = async () => {
      let result = await getAllTodos();
      if (result.length > 0) {
        settodos(result);
      }
    };
    getAll();
    console.log("calls made useEffect ", {
      // isGetAll,
      // isDeleted,
      isInserted,
      isDeleteAll,
    });
  }, [isDeleteAll, isInserted]);

  const insertOneTodo = async () => {
    insertTodo(todoTask);
    setisInserted(!isInserted);
    settodoTask("");
  };
  const getOne = async () => {
    setOneTodo(null);
    let result2 = await getOneTodo(todo);
    if (result2.length > 0) {
      console.log("data by getone : ", result2.length);
      setOneTodo(result2);
      setShowAlert(true);
    }
  };
  const deleteAll = async () => {
    await deleteAllTodos(); // Wait for the delete operation to complete
    setIsDeleteAll(!isDeleteAll);
  };
  // isGetAll, isDeleted, isInserted, isDeleteAll;
  return (
    <>
      <Navbar bg="dark" expand="sm" variant="dark">
        <Container fluid>
          <Navbar.Brand className="mx-auto">React TodoList</Navbar.Brand>
        </Container>
      </Navbar>
      <Container className="d-flex justify-content-center">
        <Card className="shadow-lg">
          <Card.Body>
            <div>
              <Input
                placeholder="enter task"
                variant="light"
                animation="glow"
                xs={4}
                value={todoTask}
                onChange={(event) => settodoTask(event.target.value)}
              ></Input>
              <Button
                className=""
                variant="dark"
                expand="sm"
                onClick={insertOneTodo}
              >
                ADD
              </Button>
              <Button
                className=""
                variant="dark"
                expand="sm"
                onClick={deleteAll}
              >
                Delete All
              </Button>
            </div>
            <Table bordered striped responsive>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>TASK</th>
                </tr>
              </thead>
              <tbody>
                {todos.map((item) => (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.task}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
            <div>
              <Input
                placeholder="enter id"
                variant="light"
                animation="glow"
                xs={4}
                value={todo}
                onChange={(event) => settodo(event.target.value)}
              ></Input>
              <Button className="" variant="dark" expand="sm" onClick={getOne}>
                GET
              </Button>
            </div>
            <div
              style={{
                padding: "10px",
                overflow: "auto",
                whiteSpace: "pre-wrap",
                color: "black",
              }}
            >
              { showAlert && gegetOneT.map((item) => {
                const createdAt = new Date(item.createdAt);
                const formattedDate = createdAt.toDateString();

                return (
                  <div key={item.id}>
                    <p>ID: {item.id}</p>
                    <p>TASK: {item.task}</p>
                    <p>CreatedAt: {formattedDate}</p>
                  </div>
                );
              })}
            </div>
          </Card.Body>
        </Card>
      </Container>
    </>
  );
}
export default AllTodo;

